mapper.select:
SELECT a.id,a.room_name,a.room_type,a.room_focus,a.room_volume,a.room_join_account, a.address,a.start_time,a.deadline,a.end_time,a.deposit,b.status,a.room_describtion,a.phone, a.name,a.email,a.shop_name,a.menu_describtion,a.total_price,a.room_topic,a.member,a.car_array FROM room a LEFT JOIN room_user b ON a.id = b.room_id WHERE b.open_id = #{openId}
AND b.status = #{status}
ORDER BY a.create_time DESC

mapper.update:
UPDATE room_user t


        t.status = #{status}


WHERE t.room_id= #{roomId} AND t.open_id = #{openId}
